#!/bin/bash
  
df=`df -g . | awk '{n=$4}END{print n}'`
if [ $df -eq 0 ]; then echo "No space"; exit; fi

pid=${0%%sh}pid
if [ -f $pid ]; then echo "Found $pid"; exit; fi
echo $$ > $pid

panic=${0%%sh}panic
if [ -e $panic ]; then echo "Fanic $panic"; exit; fi

function check_ret {
  msg=$1; shift
  for ret in $*; do
    if [ $ret -ne 0 ]; then
      echo $msg $* > $panic
      locate mail
      if [ $? -eq 0 ]; then
        echo $msg $* | mail -s "Error in $msg" bil022@ucsd.edu
      fi
      exit
    fi
  done
}

